select transaksi.id, transaksi.tanggal_order,transaksi.status_pelunasan,transaksi.tanggal_pembayaran, sum(detail_transaction.subtotal) as total, sum(detail_transaction.jumlah) as jumlah from transaksi inner join detail_transaction on transaksi.id=detail_transaction.id_transaksi group by detail_transaction.id_transaksi 